import React, { Component } from 'react';
import Todos from './components/Todos';

import './App.css';

class App extends Component {
  state = {
    todos:[
      {
        id:1,
        title:'Take out the trash',
        completed:false
      },
      {
        id:2,
        title:'Dinner with Jasmine',
        completed:false
      },
      {
        id:3,
        title:'Meeting with boss',
        completed:false
      },
    ]
  }
// Dete To do 
delTodo = (id) => {
  this.setState({ todo: [...this.state.todos.filter(todo=>todo.id 
  !== id)]});
}

//Toggle complete
  markComplete = (id) => {
    this.setState({ todos: this.state.todos.map(todo => {
      if(todo.id === id){
        todo.completed = !todo.completed
      }
      return todo;
    }) });
  }

  render() { // * this is lifecyle method and it is required to render the component in the browser
    return ( // this render returing jsx its basically easier way to output javascript in the browser
      <div className="App">
       <Todos todos={this.state.todos} markComplete={this.markComplete} 
       delTodo={this.delTodo} />
      </div>
    );
  }
}

export default App;
